sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Only display What needed choices that match the Request type value.

	var whatneeded = g_form.getValue('u_what_needed');

	g_form.clearOptions('u_what_needed');

	if ( newValue == 'hr') {

		g_form.addOption('u_what_needed','Hx1','Human Rescources 1');
		g_form.addOption('u_what_needed','Hx2','Human Rescources 2');
		g_form.addOption('u_what_needed','other','other');
	}

	if ( newValue == 'legal') {

		g_form.addOption('u_what_needed','Hx1','legal 1');
		g_form.addOption('u_what_needed','Hx2','legal 2');
		g_form.addOption('u_what_needed','Hx3','other');
	}

	if ( newValue == 'facilities') {

		g_form.addOption('u_what_needed','Hx1','facalities 1');
		g_form.addOption('u_what_needed','Hx2','facalities 2');
		g_form.addOption('u_what_needed','Hx3','other');
	}

	if ( isLoading && !g_form.isNewRecord()) {

		g_form.setValue('u_what_needed',whatneeded)
	}

}
